import logo from './logo.svg';
import './App.css';
// import 'bootstrap/dist/css/bootstrap.min.css';
import image from './images/decentraland_map_01.png';
import image2 from "./images/decentraland_map_02.gif"; 
import image3 from "./images/decentraland_map_03.gif"
import image4 from "./images/decentraland_map_04.gif"
import image5 from "./images/decentraland_map_05.gif";
import image6 from "./images/decentraland_map_06.gif"
import image7 from "./images/decentraland_map_07.gif"
import image8 from "./images/decentraland_map_08.gif"
import image9 from "./images/decentraland_map_09.gif"
import headerImage from "./images/Decentraldatalogo.png";

import Button from 'react-bootstrap/Button'
import { render } from '@testing-library/react';
import { Component } from 'react'
import Axios from 'axios'

class App extends Component {
  constructor() {
    super();
    this.show1 = this.show1.bind(this)
    this.viewmore = this.viewmore.bind(this)
    this.state = {
      sector1: false,
      sector2: false,
      sector3: false,
      sector4: false,
      sector5: false,
      sector6: false,
      sector7: false,
      sector8: false,
      sector9: false,
      cards: "Not availible",
      images: [],
      length: 4
    }
  }

  componentDidMount() {
    // var array;
    // Axios.get('https://api.decentraland.org/v1/parcels/23/33/map.png').then(res => array.push(res.data))
    // this.setState({images: array})
  }

  viewmore() {
    var lengthTemp = this.state.length + 4;
    this.setState({length: lengthTemp})
  }

  show1() {
    this.setState({sector1: !this.state.sector1})
    console.log('not showing')
  }

  render() {
    var i;
    var recommended = []
    for(i = 0; i < this.state.length; i++) {
      recommended.push(
        <div class="card">
            <div class="image">
            </div>
            <div class="title">
                <h1 class="name">NFT Gallery</h1>
                <img src='https://api.decentraland.org/v1/parcels/23/33/map.png' class="cardImage"></img>
                <h1 class="coord">(101,25)</h1>
                <h1>Expected growth:
                    250%
                </h1>
                <h1> Current price:<br></br>
                    20000</h1>
                <h2>Time till expiration: 12 days</h2>
            </div>
            <div class="des">
              <button variant="primary">View listing</button>
            </div>
      </div>)
    }
  return (
    <div className="App">
      <header class="headr">
        <div class="logo">
            <a href=""><img src={headerImage} alt=""></img></a>
        </div>
    </header>
      <h5>Click on the part to explore!</h5>
      <section id="mapd">
          <div class="maph">
              <div class="map">
                  <img src={image} onClick={this.show1} alt="" class="mapp" id="map1"></img>
                  <img src={image2} alt="" class="mapp" id="map2"></img>
                  <img src={image3} alt="" class="mapp" id="map3"></img>
                  <img src={image4} alt="" class="mapp" id="map4"></img>
                  <img src={image5} alt="" class="mapp" id="map5"></img>
                  <img src={image6} alt="" class="mapp" id="map6"></img>
                  <img src={image7} alt="" class="mapp" id="map7"></img>
                  <img src={image8}  alt="" class="mapp" id="map8"></img>
                  <img src={image9} alt="" class="mapp" id="map9"></img>
              </div>
          </div>
      </section>
      <section id="data">
        {this.state.sector1 ?
        <div class="cards">
            <div class="heading">
                <h1> <br></br> Sector 1</h1>
            </div>
            <div>
              {this.state.cards}
            </div>
            {recommended}
            <button className="cards">View More</button>
        </div>
      : <div></div>}
    </section>

      <link rel="preconnect" href="https://fonts.gstatic.com"></link>
      <link href="https://fonts.googleapis.com/css2?family=Monda&display=swap" rel="stylesheet"></link>
      {/* <link
        rel="stylesheet"
        href="https://cdn.jsdelivr.net/npm/bootstrap@4.6.0/dist/css/bootstrap.min.css"
        integrity="sha384-B0vP5xmATw1+K9KRQjQERJvTumQW0nPEzvF6L/Z6nronJ3oUOFUFpCjEUQouq2+l"
        crossorigin="anonymous"
      /> */}
    </div>
  )};
}

export default App;
